import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Formik, Form, Field } from 'formik';
import * as Yup from 'yup';
import {
  Box,
  Typography,
  Paper,
  Grid,
  TextField,
  Button,
  MenuItem,
  FormControl,
  InputLabel,
  Select,
  IconButton,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  Divider,
  Alert,
  FormControlLabel,
  Switch,
} from '@mui/material';
import { Delete as DeleteIcon, Add as AddIcon } from '@mui/icons-material';

interface AdditionalApplicant {
  name: string;
  email: string;
  employmentStatus?: string;
  annualIncome?: string;
  creditScore?: string;
}

interface FormValues {
  caseType: string;
  description: string;
  propertyType: string;
  estimatedValue: string;
  loanAmount: string;
  employmentStatus: string;
  annualIncome: string;
  depositAmount: string;
  propertyAddress: string;
  propertyPostcode: string;
  termLength: string;
  interestRateType: string;
  creditScore: string;
  existingMortgage: {
    hasExistingMortgage: boolean;
    currentLender?: string;
    currentBalance?: string;
    currentRate?: string;
    currentTerm?: string;
  };
  additionalApplicants: AdditionalApplicant[];
}

interface AdditionalApplicantErrors {
  name?: string;
  email?: string;
  employmentStatus?: string;
  annualIncome?: string;
  creditScore?: string;
}

const validationSchema = Yup.object({
  caseType: Yup.string().required('Case type is required'),
  description: Yup.string().required('Description is required'),
  propertyType: Yup.string().required('Property type is required'),
  estimatedValue: Yup.number()
    .required('Estimated value is required')
    .positive('Value must be positive'),
  loanAmount: Yup.number()
    .required('Loan amount is required')
    .positive('Amount must be positive'),
  employmentStatus: Yup.string().required('Employment status is required'),
  annualIncome: Yup.number()
    .required('Annual income is required')
    .positive('Income must be positive'),
  depositAmount: Yup.number()
    .required('Deposit amount is required')
    .positive('Deposit must be positive'),
  propertyAddress: Yup.string().required('Property address is required'),
  propertyPostcode: Yup.string()
    .required('Postcode is required')
    .matches(/^[A-Z]{1,2}[0-9][A-Z0-9]? ?[0-9][A-Z]{2}$/i, 'Invalid UK postcode'),
  termLength: Yup.string().required('Term length is required'),
  interestRateType: Yup.string().required('Interest rate type is required'),
  creditScore: Yup.number()
    .required('Credit score is required')
    .min(300, 'Credit score must be at least 300')
    .max(850, 'Credit score must not exceed 850'),
  existingMortgage: Yup.object({
    hasExistingMortgage: Yup.boolean(),
    currentLender: Yup.string().when('hasExistingMortgage', {
      is: true,
      then: Yup.string().required('Current lender is required'),
    }),
    currentBalance: Yup.number().when('hasExistingMortgage', {
      is: true,
      then: Yup.number()
        .required('Current balance is required')
        .positive('Balance must be positive'),
    }),
    currentRate: Yup.number().when('hasExistingMortgage', {
      is: true,
      then: Yup.number()
        .required('Current rate is required')
        .positive('Rate must be positive'),
    }),
    currentTerm: Yup.string().when('hasExistingMortgage', {
      is: true,
      then: Yup.string().required('Current term is required'),
    }),
  }),
  additionalApplicants: Yup.array().of(
    Yup.object({
      name: Yup.string().required('Name is required'),
      email: Yup.string().email('Invalid email').required('Email is required'),
      employmentStatus: Yup.string().required('Employment status is required'),
      annualIncome: Yup.number()
        .required('Annual income is required')
        .positive('Income must be positive'),
      creditScore: Yup.number()
        .required('Credit score is required')
        .min(300, 'Credit score must be at least 300')
        .max(850, 'Credit score must not exceed 850'),
    })
  ),
});

const initialValues: FormValues = {
  caseType: '',
  description: '',
  propertyType: '',
  estimatedValue: '',
  loanAmount: '',
  employmentStatus: '',
  annualIncome: '',
  depositAmount: '',
  propertyAddress: '',
  propertyPostcode: '',
  termLength: '',
  interestRateType: '',
  creditScore: '',
  existingMortgage: {
    hasExistingMortgage: false,
    currentLender: '',
    currentBalance: '',
    currentRate: '',
    currentTerm: '',
  },
  additionalApplicants: [],
};

const CreateCase: React.FC = () => {
  const navigate = useNavigate();
  const [error, setError] = useState<string | null>(null);

  const handleSubmit = async (values: typeof initialValues) => {
    try {
      // In a real application, this would be an API call
      console.log('Form submitted:', values);

      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));

      // Navigate to cases list on success
      navigate('/client/cases');
    } catch (err) {
      setError('Failed to create case. Please try again.');
    }
  };

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        Create New Case
      </Typography>

      <Paper sx={{ p: 3 }}>
        <Formik<FormValues>
          initialValues={initialValues}
          validationSchema={validationSchema}
          onSubmit={handleSubmit}
        >
          {({ values, errors, touched, handleChange, setFieldValue }) => {
            const getApplicantError = (index: number, field: keyof AdditionalApplicant): string | undefined => {
              const error = errors.additionalApplicants?.[index];
              if (typeof error === 'object' && error !== null) {
                return (error as AdditionalApplicantErrors)[field];
              }
              return undefined;
            };

            return (
              <Form>
                <Grid container spacing={3}>
                  <Grid item xs={12}>
                    <FormControl fullWidth error={touched.caseType && !!errors.caseType}>
                      <InputLabel>Case Type</InputLabel>
                      <Select
                        name="caseType"
                        value={values.caseType}
                        onChange={handleChange}
                        label="Case Type"
                      >
                        <MenuItem value="mortgage_application">Mortgage Application</MenuItem>
                        <MenuItem value="remortgage">Remortgage</MenuItem>
                        <MenuItem value="buy_to_let">Buy to Let</MenuItem>
                      </Select>
                      {touched.caseType && errors.caseType && (
                        <Typography color="error" variant="caption">
                          {errors.caseType}
                        </Typography>
                      )}
                    </FormControl>
                  </Grid>

                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      name="description"
                      label="Description"
                      value={values.description}
                      onChange={handleChange}
                      error={touched.description && !!errors.description}
                      helperText={touched.description && errors.description}
                      multiline
                      rows={3}
                    />
                  </Grid>

                  <Grid item xs={12}>
                    <Typography variant="h6" gutterBottom>
                      Property Details
                    </Typography>
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <FormControl fullWidth error={touched.propertyType && !!errors.propertyType}>
                      <InputLabel>Property Type</InputLabel>
                      <Select
                        name="propertyType"
                        value={values.propertyType}
                        onChange={handleChange}
                        label="Property Type"
                      >
                        <MenuItem value="house">House</MenuItem>
                        <MenuItem value="flat">Flat</MenuItem>
                        <MenuItem value="bungalow">Bungalow</MenuItem>
                        <MenuItem value="maisonette">Maisonette</MenuItem>
                      </Select>
                      {touched.propertyType && errors.propertyType && (
                        <Typography color="error" variant="caption">
                          {errors.propertyType}
                        </Typography>
                      )}
                    </FormControl>
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <TextField
                      fullWidth
                      name="propertyAddress"
                      label="Property Address"
                      value={values.propertyAddress}
                      onChange={handleChange}
                      error={touched.propertyAddress && !!errors.propertyAddress}
                      helperText={touched.propertyAddress && errors.propertyAddress}
                      multiline
                      rows={2}
                    />
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <TextField
                      fullWidth
                      name="propertyPostcode"
                      label="Postcode"
                      value={values.propertyPostcode}
                      onChange={handleChange}
                      error={touched.propertyPostcode && !!errors.propertyPostcode}
                      helperText={touched.propertyPostcode && errors.propertyPostcode}
                    />
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <TextField
                      fullWidth
                      name="estimatedValue"
                      label="Estimated Property Value"
                      type="number"
                      value={values.estimatedValue}
                      onChange={handleChange}
                      error={touched.estimatedValue && !!errors.estimatedValue}
                      helperText={touched.estimatedValue && errors.estimatedValue}
                    />
                  </Grid>

                  <Grid item xs={12}>
                    <Typography variant="h6" gutterBottom>
                      Financial Details
                    </Typography>
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <TextField
                      fullWidth
                      name="loanAmount"
                      label="Loan Amount"
                      type="number"
                      value={values.loanAmount}
                      onChange={handleChange}
                      error={touched.loanAmount && !!errors.loanAmount}
                      helperText={touched.loanAmount && errors.loanAmount}
                    />
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <TextField
                      fullWidth
                      name="depositAmount"
                      label="Deposit Amount"
                      type="number"
                      value={values.depositAmount}
                      onChange={handleChange}
                      error={touched.depositAmount && !!errors.depositAmount}
                      helperText={touched.depositAmount && errors.depositAmount}
                    />
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <FormControl fullWidth error={touched.termLength && !!errors.termLength}>
                      <InputLabel>Term Length</InputLabel>
                      <Select
                        name="termLength"
                        value={values.termLength}
                        onChange={handleChange}
                        label="Term Length"
                      >
                        <MenuItem value="25">25 years</MenuItem>
                        <MenuItem value="30">30 years</MenuItem>
                        <MenuItem value="35">35 years</MenuItem>
                        <MenuItem value="40">40 years</MenuItem>
                      </Select>
                      {touched.termLength && errors.termLength && (
                        <Typography color="error" variant="caption">
                          {errors.termLength}
                        </Typography>
                      )}
                    </FormControl>
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <FormControl fullWidth error={touched.interestRateType && !!errors.interestRateType}>
                      <InputLabel>Interest Rate Type</InputLabel>
                      <Select
                        name="interestRateType"
                        value={values.interestRateType}
                        onChange={handleChange}
                        label="Interest Rate Type"
                      >
                        <MenuItem value="fixed">Fixed Rate</MenuItem>
                        <MenuItem value="variable">Variable Rate</MenuItem>
                        <MenuItem value="tracker">Tracker Rate</MenuItem>
                      </Select>
                      {touched.interestRateType && errors.interestRateType && (
                        <Typography color="error" variant="caption">
                          {errors.interestRateType}
                        </Typography>
                      )}
                    </FormControl>
                  </Grid>

                  <Grid item xs={12}>
                    <Paper sx={{ p: 2, bgcolor: 'background.default' }}>
                      <Typography variant="subtitle1" gutterBottom>
                        Loan-to-Value Ratio Summary
                      </Typography>
                      <Grid container spacing={2}>
                        <Grid item xs={12} md={4}>
                          <Typography variant="body2" color="text.secondary">
                            Property Value: £{values.estimatedValue || '0'}
                          </Typography>
                        </Grid>
                        <Grid item xs={12} md={4}>
                          <Typography variant="body2" color="text.secondary">
                            Loan Amount: £{values.loanAmount || '0'}
                          </Typography>
                        </Grid>
                        <Grid item xs={12} md={4}>
                          <Typography variant="body2" color="text.secondary">
                            LTV: {values.estimatedValue && values.loanAmount 
                              ? `${((Number(values.loanAmount) / Number(values.estimatedValue)) * 100).toFixed(1)}%`
                              : '0%'}
                          </Typography>
                        </Grid>
                      </Grid>
                    </Paper>
                  </Grid>

                  <Grid item xs={12}>
                    <Typography variant="h6" gutterBottom>
                      Credit Information
                    </Typography>
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <TextField
                      fullWidth
                      name="creditScore"
                      label="Credit Score"
                      type="number"
                      value={values.creditScore}
                      onChange={handleChange}
                      error={touched.creditScore && !!errors.creditScore}
                      helperText={touched.creditScore && errors.creditScore}
                      inputProps={{ min: 300, max: 850 }}
                    />
                  </Grid>

                  <Grid item xs={12}>
                    <Typography variant="h6" gutterBottom>
                      Existing Mortgage Details
                    </Typography>
                  </Grid>

                  <Grid item xs={12}>
                    <FormControlLabel
                      control={
                        <Switch
                          checked={values.existingMortgage.hasExistingMortgage}
                          onChange={(e) => {
                            setFieldValue('existingMortgage.hasExistingMortgage', e.target.checked);
                            if (!e.target.checked) {
                              setFieldValue('existingMortgage', {
                                hasExistingMortgage: false,
                                currentLender: '',
                                currentBalance: '',
                                currentRate: '',
                                currentTerm: '',
                              });
                            }
                          }}
                        />
                      }
                      label="I have an existing mortgage"
                    />
                  </Grid>

                  {values.existingMortgage.hasExistingMortgage && (
                    <>
                      <Grid item xs={12} md={6}>
                        <TextField
                          fullWidth
                          name="existingMortgage.currentLender"
                          label="Current Lender"
                          value={values.existingMortgage.currentLender}
                          onChange={handleChange}
                          error={
                            touched.existingMortgage?.currentLender &&
                            !!errors.existingMortgage?.currentLender
                          }
                          helperText={
                            touched.existingMortgage?.currentLender &&
                            errors.existingMortgage?.currentLender
                          }
                        />
                      </Grid>

                      <Grid item xs={12} md={6}>
                        <TextField
                          fullWidth
                          name="existingMortgage.currentBalance"
                          label="Current Balance"
                          type="number"
                          value={values.existingMortgage.currentBalance}
                          onChange={handleChange}
                          error={
                            touched.existingMortgage?.currentBalance &&
                            !!errors.existingMortgage?.currentBalance
                          }
                          helperText={
                            touched.existingMortgage?.currentBalance &&
                            errors.existingMortgage?.currentBalance
                          }
                        />
                      </Grid>

                      <Grid item xs={12} md={6}>
                        <TextField
                          fullWidth
                          name="existingMortgage.currentRate"
                          label="Current Rate (%)"
                          type="number"
                          value={values.existingMortgage.currentRate}
                          onChange={handleChange}
                          error={
                            touched.existingMortgage?.currentRate &&
                            !!errors.existingMortgage?.currentRate
                          }
                          helperText={
                            touched.existingMortgage?.currentRate &&
                            errors.existingMortgage?.currentRate
                          }
                        />
                      </Grid>

                      <Grid item xs={12} md={6}>
                        <FormControl fullWidth>
                          <InputLabel>Current Term</InputLabel>
                          <Select
                            name="existingMortgage.currentTerm"
                            value={values.existingMortgage.currentTerm}
                            onChange={handleChange}
                            label="Current Term"
                          >
                            <MenuItem value="25">25 years</MenuItem>
                            <MenuItem value="30">30 years</MenuItem>
                            <MenuItem value="35">35 years</MenuItem>
                            <MenuItem value="40">40 years</MenuItem>
                          </Select>
                          {touched.existingMortgage?.currentTerm &&
                            errors.existingMortgage?.currentTerm && (
                              <Typography color="error" variant="caption">
                                {errors.existingMortgage.currentTerm}
                              </Typography>
                            )}
                        </FormControl>
                      </Grid>
                    </>
                  )}

                  <Grid item xs={12}>
                    <Typography variant="h6" gutterBottom>
                      Primary Applicant Details
                    </Typography>
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <FormControl fullWidth error={touched.employmentStatus && !!errors.employmentStatus}>
                      <InputLabel>Employment Status</InputLabel>
                      <Select
                        name="employmentStatus"
                        value={values.employmentStatus}
                        onChange={handleChange}
                        label="Employment Status"
                      >
                        <MenuItem value="employed">Employed</MenuItem>
                        <MenuItem value="self_employed">Self Employed</MenuItem>
                        <MenuItem value="contractor">Contractor</MenuItem>
                        <MenuItem value="retired">Retired</MenuItem>
                      </Select>
                      {touched.employmentStatus && errors.employmentStatus && (
                        <Typography color="error" variant="caption">
                          {errors.employmentStatus}
                        </Typography>
                      )}
                    </FormControl>
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <TextField
                      fullWidth
                      name="annualIncome"
                      label="Annual Income"
                      type="number"
                      value={values.annualIncome}
                      onChange={handleChange}
                      error={touched.annualIncome && !!errors.annualIncome}
                      helperText={touched.annualIncome && errors.annualIncome}
                    />
                  </Grid>

                  <Grid item xs={12}>
                    <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                      <Typography variant="h6">Additional Applicants</Typography>
                      <IconButton
                        color="primary"
                        onClick={() => {
                          setFieldValue('additionalApplicants', [
                            ...values.additionalApplicants,
                            { name: '', email: '', employmentStatus: '', annualIncome: '', creditScore: '' },
                          ]);
                        }}
                      >
                        <AddIcon />
                      </IconButton>
                    </Box>

                    <List>
                      {values.additionalApplicants.map((_, index) => (
                        <React.Fragment key={index}>
                          <ListItem>
                            <Grid container spacing={2}>
                              <Grid item xs={12} md={6}>
                                <TextField
                                  fullWidth
                                  name={`additionalApplicants.${index}.name`}
                                  label="Name"
                                  value={values.additionalApplicants[index].name}
                                  onChange={handleChange}
                                  error={
                                    touched.additionalApplicants?.[index]?.name &&
                                    !!getApplicantError(index, 'name')
                                  }
                                  helperText={
                                    touched.additionalApplicants?.[index]?.name &&
                                    getApplicantError(index, 'name')
                                  }
                                />
                              </Grid>
                              <Grid item xs={12} md={6}>
                                <TextField
                                  fullWidth
                                  name={`additionalApplicants.${index}.email`}
                                  label="Email"
                                  type="email"
                                  value={values.additionalApplicants[index].email}
                                  onChange={handleChange}
                                  error={
                                    touched.additionalApplicants?.[index]?.email &&
                                    !!getApplicantError(index, 'email')
                                  }
                                  helperText={
                                    touched.additionalApplicants?.[index]?.email &&
                                    getApplicantError(index, 'email')
                                  }
                                />
                              </Grid>
                              <Grid item xs={12} md={6}>
                                <FormControl fullWidth>
                                  <InputLabel>Employment Status</InputLabel>
                                  <Select
                                    name={`additionalApplicants.${index}.employmentStatus`}
                                    value={values.additionalApplicants[index].employmentStatus}
                                    onChange={handleChange}
                                    label="Employment Status"
                                  >
                                    <MenuItem value="employed">Employed</MenuItem>
                                    <MenuItem value="self_employed">Self Employed</MenuItem>
                                    <MenuItem value="contractor">Contractor</MenuItem>
                                    <MenuItem value="retired">Retired</MenuItem>
                                  </Select>
                                </FormControl>
                              </Grid>
                              <Grid item xs={12} md={6}>
                                <TextField
                                  fullWidth
                                  name={`additionalApplicants.${index}.annualIncome`}
                                  label="Annual Income"
                                  type="number"
                                  value={values.additionalApplicants[index].annualIncome}
                                  onChange={handleChange}
                                />
                              </Grid>
                              <Grid item xs={12} md={6}>
                                <TextField
                                  fullWidth
                                  name={`additionalApplicants.${index}.creditScore`}
                                  label="Credit Score"
                                  type="number"
                                  value={values.additionalApplicants[index].creditScore}
                                  onChange={handleChange}
                                  error={
                                    touched.additionalApplicants?.[index]?.creditScore &&
                                    !!getApplicantError(index, 'creditScore')
                                  }
                                  helperText={
                                    touched.additionalApplicants?.[index]?.creditScore &&
                                    getApplicantError(index, 'creditScore')
                                  }
                                  inputProps={{ min: 300, max: 850 }}
                                />
                              </Grid>
                            </Grid>
                            <ListItemSecondaryAction>
                              <IconButton
                                edge="end"
                                onClick={() => {
                                  const newApplicants = [...values.additionalApplicants];
                                  newApplicants.splice(index, 1);
                                  setFieldValue('additionalApplicants', newApplicants);
                                }}
                              >
                                <DeleteIcon />
                              </IconButton>
                            </ListItemSecondaryAction>
                          </ListItem>
                          {index < values.additionalApplicants.length - 1 && <Divider />}
                        </React.Fragment>
                      ))}
                    </List>
                  </Grid>

                  {error && (
                    <Grid item xs={12}>
                      <Alert severity="error">{error}</Alert>
                    </Grid>
                  )}

                  <Grid item xs={12}>
                    <Box sx={{ display: 'flex', gap: 2, justifyContent: 'flex-end' }}>
                      <Button
                        variant="outlined"
                        onClick={() => navigate('/client/cases')}
                      >
                        Cancel
                      </Button>
                      <Button
                        type="submit"
                        variant="contained"
                        color="primary"
                      >
                        Create Case
                      </Button>
                    </Box>
                  </Grid>
                </Grid>
              </Form>
            );
          }}
        </Formik>
      </Paper>
    </Box>
  );
};

export default CreateCase;
