import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ThemeProvider, createTheme } from '@mui/material';
import MainLayout from './components/layout/MainLayout';
import Dashboard from './pages/Dashboard';
import MortgageApplicationForm from './components/mortgage/MortgageApplicationForm';
import LoginPage from './components/auth/LoginPage';
import RegisterPage from './components/auth/RegisterPage';
import CreateCase from './components/cases/CreateCase';
import ClientDashboard from './components/client/ClientDashboard';
import ClientProfile from './components/client/ClientProfile';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
});

// Simple auth check - replace with proper authentication
const isAuthenticated = () => {
  return localStorage.getItem('isAuthenticated') === 'true';
};

// Get user type from localStorage
const getUserType = () => {
  return localStorage.getItem('userType');
};

// Protected Route wrapper with role check
const ProtectedRoute: React.FC<{ 
  children: React.ReactNode | (() => React.ReactNode);
  allowedRoles?: string[];
}> = ({ children, allowedRoles }) => {
  if (!isAuthenticated()) {
    return <Navigate to="/login" />;
  }

  if (allowedRoles) {
    const userType = getUserType();
    if (!userType || !allowedRoles.includes(userType)) {
      return <Navigate to="/" />;
    }
  }

  return typeof children === 'function' ? children() : <>{children}</>;
};

function App() {
  return (
    <ThemeProvider theme={theme}>
      <Router>
        <Routes>
          <Route path="/login" element={<LoginPage />} />
          <Route path="/register" element={<RegisterPage />} />

          {/* Client Routes */}
          <Route
            path="/client"
            element={
              <ProtectedRoute allowedRoles={['client']}>
                <MainLayout />
              </ProtectedRoute>
            }
          >
            <Route index element={<ClientDashboard />} />
            <Route path="profile" element={<ClientProfile />} />
            <Route path="create-case" element={<CreateCase />} />
            <Route path="case/:id" element={<div>Case Details (Coming Soon)</div>} />
          </Route>

          {/* Adviser Routes */}
          <Route
            path="/adviser"
            element={
              <ProtectedRoute allowedRoles={['adviser']}>
                <MainLayout />
              </ProtectedRoute>
            }
          >
            <Route index element={<div>Adviser Dashboard (Coming Soon)</div>} />
            <Route path="cases" element={<div>Adviser Cases (Coming Soon)</div>} />
            <Route path="profile" element={<div>Adviser Profile (Coming Soon)</div>} />
          </Route>

          {/* Default Route - Redirect based on user type */}
          <Route
            path="/"
            element={
              <ProtectedRoute>
                {() => {
                  const userType = getUserType();
                  return <Navigate to={`/${userType}`} />;
                }}
              </ProtectedRoute>
            }
          />
        </Routes>
      </Router>
    </ThemeProvider>
  );
}

export default App;
