import React, { useState } from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import {
  Box,
  Button,
  Container,
  TextField,
  Typography,
  Paper,
  Alert,
  Grid,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Chip,
  IconButton,
} from '@mui/material';
import { Add as AddIcon, Delete as DeleteIcon } from '@mui/icons-material';

const validationSchema = Yup.object({
  caseType: Yup.string().required('Case type is required'),
  description: Yup.string().required('Description is required'),
  additionalApplicants: Yup.array().of(
    Yup.object({
      email: Yup.string().email('Invalid email address').required('Email is required'),
      name: Yup.string().required('Name is required'),
    })
  ),
});

const CreateCase: React.FC = () => {
  const [error, setError] = useState<string>('');
  const [additionalApplicants, setAdditionalApplicants] = useState<Array<{ email: string; name: string }>>([]);

  const formik = useFormik({
    initialValues: {
      caseType: '',
      description: '',
      additionalApplicants: [],
    },
    validationSchema: validationSchema,
    onSubmit: async (values) => {
      try {
        // Replace with actual API call
        console.log('Case creation values:', {
          ...values,
          additionalApplicants,
        });
        // Navigate to case details or dashboard
      } catch (err) {
        setError('An error occurred while creating the case');
      }
    },
  });

  const handleAddApplicant = () => {
    setAdditionalApplicants([...additionalApplicants, { email: '', name: '' }]);
  };

  const handleRemoveApplicant = (index: number) => {
    setAdditionalApplicants(additionalApplicants.filter((_, i) => i !== index));
  };

  const handleApplicantChange = (index: number, field: 'email' | 'name', value: string) => {
    const newApplicants = [...additionalApplicants];
    newApplicants[index] = { ...newApplicants[index], [field]: value };
    setAdditionalApplicants(newApplicants);
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ mt: 4 }}>
        <Paper elevation={3} sx={{ p: 4 }}>
          <Typography variant="h4" gutterBottom>
            Create New Case
          </Typography>
          {error && (
            <Alert severity="error" sx={{ mb: 2 }}>
              {error}
            </Alert>
          )}
          <form onSubmit={formik.handleSubmit}>
            <Grid container spacing={3}>
              <Grid item xs={12}>
                <FormControl fullWidth>
                  <InputLabel id="caseType-label">Case Type</InputLabel>
                  <Select
                    labelId="caseType-label"
                    id="caseType"
                    name="caseType"
                    value={formik.values.caseType}
                    label="Case Type"
                    onChange={formik.handleChange}
                  >
                    <MenuItem value="mortgage">Mortgage Application</MenuItem>
                    <MenuItem value="refinance">Refinance</MenuItem>
                    <MenuItem value="buyToLet">Buy to Let</MenuItem>
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  multiline
                  rows={4}
                  id="description"
                  name="description"
                  label="Case Description"
                  value={formik.values.description}
                  onChange={formik.handleChange}
                  error={formik.touched.description && Boolean(formik.errors.description)}
                  helperText={formik.touched.description && formik.errors.description}
                />
              </Grid>
              <Grid item xs={12}>
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                  <Typography variant="h6">Additional Applicants</Typography>
                  <IconButton onClick={handleAddApplicant} color="primary">
                    <AddIcon />
                  </IconButton>
                </Box>
                {additionalApplicants.map((applicant, index) => (
                  <Paper key={index} sx={{ p: 2, mb: 2 }}>
                    <Grid container spacing={2} alignItems="center">
                      <Grid item xs={5}>
                        <TextField
                          fullWidth
                          label="Name"
                          value={applicant.name}
                          onChange={(e) => handleApplicantChange(index, 'name', e.target.value)}
                        />
                      </Grid>
                      <Grid item xs={5}>
                        <TextField
                          fullWidth
                          label="Email"
                          type="email"
                          value={applicant.email}
                          onChange={(e) => handleApplicantChange(index, 'email', e.target.value)}
                        />
                      </Grid>
                      <Grid item xs={2}>
                        <IconButton onClick={() => handleRemoveApplicant(index)} color="error">
                          <DeleteIcon />
                        </IconButton>
                      </Grid>
                    </Grid>
                  </Paper>
                ))}
              </Grid>
              <Grid item xs={12}>
                <Button
                  type="submit"
                  variant="contained"
                  color="primary"
                  size="large"
                  fullWidth
                >
                  Create Case
                </Button>
              </Grid>
            </Grid>
          </form>
        </Paper>
      </Box>
    </Container>
  );
};

export default CreateCase;
