import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Formik, Form, Field } from 'formik';
import * as Yup from 'yup';
import {
  Box,
  Typography,
  Paper,
  Grid,
  TextField,
  Button,
  MenuItem,
  FormControl,
  InputLabel,
  Select,
  IconButton,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  Divider,
  Alert,
  FormControlLabel,
  Switch,
} from '@mui/material';
import { Delete as DeleteIcon, Add as AddIcon } from '@mui/icons-material';

interface Loan {
  type: string;
  amount: string;
  monthlyPayment: string;
  remainingTerm: string;
}

interface CreditCard {
  provider: string;
  limit: string;
  balance: string;
  monthlyPayment: string;
}

interface AdditionalApplicant {
  name: string;
  email: string;
  employmentStatus?: string;
  annualIncome?: string;
  creditScore?: string;
  baseSalary?: string;
  bonus?: string;
  overtime?: string;
  otherIncome?: string;
  loans: Loan[];
  overdraft?: string;
  creditCards: CreditCard[];
  otherLiabilities?: string;
}

interface FormValues {
  caseType: string;
  description: string;
  propertyType: string;
  estimatedValue: string;
  loanAmount: string;
  employmentStatus: string;
  annualIncome: string;
  depositAmount: string;
  propertyAddress: string;
  propertyPostcode: string;
  termLength: string;
  interestRateType: string;
  creditScore: string;
  existingMortgage: {
    hasExistingMortgage: boolean;
    currentLender?: string;
    currentBalance?: string;
    currentRate?: string;
    currentTerm?: string;
  };
  baseSalary: string;
  bonus: string;
  overtime: string;
  otherIncome: string;
  loans: Loan[];
  overdraft: string;
  creditCards: CreditCard[];
  otherLiabilities: string;
  additionalApplicants: AdditionalApplicant[];
}

interface AdditionalApplicantErrors {
  name?: string;
  email?: string;
  employmentStatus?: string;
  annualIncome?: string;
  creditScore?: string;
  baseSalary?: string;
  bonus?: string;
  overtime?: string;
  otherIncome?: string;
  loans?: Array<{
    type?: string;
    amount?: string;
    monthlyPayment?: string;
    remainingTerm?: string;
  }>;
  overdraft?: string;
  creditCards?: Array<{
    provider?: string;
    limit?: string;
    balance?: string;
    monthlyPayment?: string;
  }>;
  otherLiabilities?: string;
}

const validationSchema = Yup.object({
  caseType: Yup.string().required('Case type is required'),
  description: Yup.string().required('Description is required'),
  propertyType: Yup.string().required('Property type is required'),
  estimatedValue: Yup.number()
    .required('Estimated value is required')
    .positive('Value must be positive'),
  loanAmount: Yup.number()
    .required('Loan amount is required')
    .positive('Amount must be positive'),
  employmentStatus: Yup.string().required('Employment status is required'),
  annualIncome: Yup.number()
    .required('Annual income is required')
    .positive('Income must be positive'),
  depositAmount: Yup.number()
    .required('Deposit amount is required')
    .positive('Deposit must be positive'),
  propertyAddress: Yup.string().required('Property address is required'),
  propertyPostcode: Yup.string()
    .required('Postcode is required')
    .matches(/^[A-Z]{1,2}[0-9][A-Z0-9]? ?[0-9][A-Z]{2}$/i, 'Invalid UK postcode'),
  termLength: Yup.string().required('Term length is required'),
  interestRateType: Yup.string().required('Interest rate type is required'),
  creditScore: Yup.number()
    .required('Credit score is required')
    .min(300, 'Credit score must be at least 300')
    .max(850, 'Credit score must not exceed 850'),
  existingMortgage: Yup.object({
    hasExistingMortgage: Yup.boolean(),
    currentLender: Yup.string().when('hasExistingMortgage', (hasExistingMortgage, schema) => {
      return hasExistingMortgage ? schema.required('Current lender is required') : schema;
    }),
    currentBalance: Yup.number().when('hasExistingMortgage', (hasExistingMortgage, schema) => {
      return hasExistingMortgage ? schema.required('Current balance is required').positive('Balance must be positive') : schema;
    }),
    currentRate: Yup.number().when('hasExistingMortgage', (hasExistingMortgage, schema) => {
      return hasExistingMortgage ? schema.required('Current rate is required').positive('Rate must be positive') : schema;
    }),
    currentTerm: Yup.string().when('hasExistingMortgage', (hasExistingMortgage, schema) => {
      return hasExistingMortgage ? schema.required('Current term is required') : schema;
    }),
  }),
  baseSalary: Yup.number()
    .required('Base salary is required')
    .positive('Amount must be positive'),
  bonus: Yup.number()
    .min(0, 'Amount cannot be negative'),
  overtime: Yup.number()
    .min(0, 'Amount cannot be negative'),
  otherIncome: Yup.number()
    .min(0, 'Amount cannot be negative'),
  loans: Yup.array().of(
    Yup.object({
      type: Yup.string().required('Loan type is required'),
      amount: Yup.number()
        .required('Amount is required')
        .positive('Amount must be positive'),
      monthlyPayment: Yup.number()
        .required('Monthly payment is required')
        .positive('Amount must be positive'),
      remainingTerm: Yup.string().required('Remaining term is required'),
    })
  ),
  overdraft: Yup.number()
    .min(0, 'Amount cannot be negative'),
  creditCards: Yup.array().of(
    Yup.object({
      provider: Yup.string().required('Provider is required'),
      limit: Yup.number()
        .required('Limit is required')
        .positive('Amount must be positive'),
      balance: Yup.number()
        .required('Balance is required')
        .min(0, 'Amount cannot be negative'),
      monthlyPayment: Yup.number()
        .required('Monthly payment is required')
        .positive('Amount must be positive'),
    })
  ),
  otherLiabilities: Yup.string(),
  additionalApplicants: Yup.array().of(
    Yup.object({
      name: Yup.string().required('Name is required'),
      email: Yup.string().email('Invalid email').required('Email is required'),
      employmentStatus: Yup.string().required('Employment status is required'),
      annualIncome: Yup.number()
        .required('Annual income is required')
        .positive('Income must be positive'),
      creditScore: Yup.number()
        .required('Credit score is required')
        .min(300, 'Credit score must be at least 300')
        .max(850, 'Credit score must not exceed 850'),
      baseSalary: Yup.number()
        .required('Base salary is required')
        .positive('Amount must be positive'),
      bonus: Yup.number()
        .min(0, 'Amount cannot be negative'),
      overtime: Yup.number()
        .min(0, 'Amount cannot be negative'),
      otherIncome: Yup.number()
        .min(0, 'Amount cannot be negative'),
      loans: Yup.array().of(
        Yup.object({
          type: Yup.string().required('Loan type is required'),
          amount: Yup.number()
            .required('Amount is required')
            .positive('Amount must be positive'),
          monthlyPayment: Yup.number()
            .required('Monthly payment is required')
            .positive('Amount must be positive'),
          remainingTerm: Yup.string().required('Remaining term is required'),
        })
      ),
      overdraft: Yup.number()
        .min(0, 'Amount cannot be negative'),
      creditCards: Yup.array().of(
        Yup.object({
          provider: Yup.string().required('Provider is required'),
          limit: Yup.number()
            .required('Limit is required')
            .positive('Amount must be positive'),
          balance: Yup.number()
            .required('Balance is required')
            .min(0, 'Amount cannot be negative'),
          monthlyPayment: Yup.number()
            .required('Monthly payment is required')
            .positive('Amount must be positive'),
        })
      ),
      otherLiabilities: Yup.string(),
    })
  ),
});

const initialValues: FormValues = {
  caseType: '',
  description: '',
  propertyType: '',
  estimatedValue: '',
  loanAmount: '',
  employmentStatus: '',
  annualIncome: '',
  depositAmount: '',
  propertyAddress: '',
  propertyPostcode: '',
  termLength: '',
  interestRateType: '',
  creditScore: '',
  existingMortgage: {
    hasExistingMortgage: false,
    currentLender: '',
    currentBalance: '',
    currentRate: '',
    currentTerm: '',
  },
  baseSalary: '',
  bonus: '',
  overtime: '',
  otherIncome: '',
  loans: [],
  overdraft: '',
  creditCards: [],
  otherLiabilities: '',
  additionalApplicants: [],
};

const CreateCase: React.FC = () => {
  const navigate = useNavigate();
  const [error, setError] = useState<string | null>(null);

  const handleSubmit = async (values: typeof initialValues) => {
    try {
      // In a real application, this would be an API call
      console.log('Form submitted:', values);

      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));

      // Navigate to cases list on success
      navigate('/client/cases');
    } catch (err) {
      setError('Failed to create case. Please try again.');
    }
  };

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        Create New Case
      </Typography>

      <Paper sx={{ p: 3 }}>
        <Formik<FormValues>
          initialValues={initialValues}
          validationSchema={validationSchema}
          onSubmit={handleSubmit}
        >
          {({ values, errors, touched, handleChange, setFieldValue }) => {
            const getApplicantError = (index: number, field: keyof AdditionalApplicant): string | undefined => {
              const error = errors.additionalApplicants?.[index];
              if (error && typeof error === 'object') {
                const typedError = error as AdditionalApplicantErrors;
                if (field === 'loans' || field === 'creditCards') {
                  return undefined; // Skip array fields
                }
                return typedError[field];
              }
              return undefined;
