                                              >
                                                <MenuItem value="personal">Personal Loan</MenuItem>
                                                <MenuItem value="car">Car Loan</MenuItem>
                                                <MenuItem value="student">Student Loan</MenuItem>
                                                <MenuItem value="other">Other</MenuItem>
                                              </Select>
                                            </FormControl>
                                          </Grid>
                                          <Grid item xs={12} md={6}>
                                            <TextField
                                              fullWidth
                                              name={`additionalApplicants.${index}.loans.${loanIndex}.amount`}
                                              label="Amount"
                                              type="number"
                                              value={values.additionalApplicants[index].loans[loanIndex].amount}
                                              onChange={handleChange}
                                            />
                                          </Grid>
                                          <Grid item xs={12} md={6}>
                                            <TextField
                                              fullWidth
                                              name={`additionalApplicants.${index}.loans.${loanIndex}.monthlyPayment`}
                                              label="Monthly Payment"
                                              type="number"
                                              value={values.additionalApplicants[index].loans[loanIndex].monthlyPayment}
                                              onChange={handleChange}
                                            />
                                          </Grid>
                                          <Grid item xs={12} md={6}>
                                            <TextField
                                              fullWidth
                                              name={`additionalApplicants.${index}.loans.${loanIndex}.remainingTerm`}
                                              label="Remaining Term (months)"
                                              type="number"
                                              value={values.additionalApplicants[index].loans[loanIndex].remainingTerm}
                                              onChange={handleChange}
                                            />
                                          </Grid>
                                        </Grid>
                                        <ListItemSecondaryAction>
                                          <IconButton
                                            edge="end"
                                            onClick={() => {
                                              const newApplicants = [...values.additionalApplicants];
                                              newApplicants[index] = {
                                                ...newApplicants[index],
                                                loans: newApplicants[index].loans.filter((_, i) => i !== loanIndex),
                                              };
                                              setFieldValue('additionalApplicants', newApplicants);
                                            }}
                                          >
                                            <DeleteIcon />
                                          </IconButton>
                                        </ListItemSecondaryAction>
                                      </ListItem>
                                      {loanIndex < (values.additionalApplicants[index].loans?.length || 0) - 1 && <Divider />}
                                    </React.Fragment>
                                  ))}
                                </List>
                              </Grid>
                              <Grid item xs={12} md={6}>
                                <TextField
                                  fullWidth
                                  name={`additionalApplicants.${index}.overdraft`}
                                  label="Overdraft Limit"
                                  type="number"
                                  value={values.additionalApplicants[index].overdraft}
                                  onChange={handleChange}
                                />
                              </Grid>
                              <Grid item xs={12}>
                                <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                                  <Typography variant="subtitle2">Credit Cards</Typography>
                                  <IconButton
                                    color="primary"
                                    onClick={() => {
                                      const newApplicants = [...values.additionalApplicants];
                                      newApplicants[index] = {
                                        ...newApplicants[index],
                                        creditCards: [
                                          ...(newApplicants[index].creditCards || []),
                                          { provider: '', limit: '', balance: '', monthlyPayment: '' },
                                        ],
                                      };
                                      setFieldValue('additionalApplicants', newApplicants);
                                    }}
                                  >
                                    <AddIcon />
                                  </IconButton>
                                </Box>
                                <List>
                                  {values.additionalApplicants[index].creditCards?.map((_, cardIndex) => (
                                    <React.Fragment key={cardIndex}>
                                      <ListItem>
                                        <Grid container spacing={2}>
                                          <Grid item xs={12} md={6}>
                                            <TextField
                                              fullWidth
                                              name={`additionalApplicants.${index}.creditCards.${cardIndex}.provider`}
                                              label="Provider"
                                              value={values.additionalApplicants[index].creditCards[cardIndex].provider}
                                              onChange={handleChange}
                                            />
                                          </Grid>
                                          <Grid item xs={12} md={6}>
                                            <TextField
                                              fullWidth
                                              name={`additionalApplicants.${index}.creditCards.${cardIndex}.limit`}
                                              label="Credit Limit"
                                              type="number"
                                              value={values.additionalApplicants[index].creditCards[cardIndex].limit}
                                              onChange={handleChange}
                                            />
                                          </Grid>
                                          <Grid item xs={12} md={6}>
                                            <TextField
                                              fullWidth
                                              name={`additionalApplicants.${index}.creditCards.${cardIndex}.balance`}
                                              label="Current Balance"
                                              type="number"
                                              value={values.additionalApplicants[index].creditCards[cardIndex].balance}
                                              onChange={handleChange}
                                            />
                                          </Grid>
                                          <Grid item xs={12} md={6}>
                                            <TextField
                                              fullWidth
                                              name={`additionalApplicants.${index}.creditCards.${cardIndex}.monthlyPayment`}
                                              label="Monthly Payment"
                                              type="number"
                                              value={values.additionalApplicants[index].creditCards[cardIndex].monthlyPayment}
                                              onChange={handleChange}
                                            />
                                          </Grid>
                                        </Grid>
                                        <ListItemSecondaryAction>
                                          <IconButton
                                            edge="end"
                                            onClick={() => {
                                              const newApplicants = [...values.additionalApplicants];
                                              newApplicants[index] = {
                                                ...newApplicants[index],
                                                creditCards: newApplicants[index].creditCards.filter((_, i) => i !== cardIndex),
                                              };
                                              setFieldValue('additionalApplicants', newApplicants);
                                            }}
                                          >
                                            <DeleteIcon />
                                          </IconButton>
                                        </ListItemSecondaryAction>
                                      </ListItem>
                                      {cardIndex < (values.additionalApplicants[index].creditCards?.length || 0) - 1 && <Divider />}
                                    </React.Fragment>
                                  ))}
                                </List>
                              </Grid>
                              <Grid item xs={12}>
                                <TextField
                                  fullWidth
                                  name={`additionalApplicants.${index}.otherLiabilities`}
                                  label="Other Liabilities"
                                  value={values.additionalApplicants[index].otherLiabilities}
                                  onChange={handleChange}
                                  multiline
                                  rows={2}
                                />
                              </Grid>
                            </Grid>
                            <ListItemSecondaryAction>
                              <IconButton
                                edge="end"
                                onClick={() => {
                                  const newApplicants = [...values.additionalApplicants];
                                  newApplicants.splice(index, 1);
                                  setFieldValue('additionalApplicants', newApplicants);
                                }}
                              >
                                <DeleteIcon />
                              </IconButton>
                            </ListItemSecondaryAction>
                          </ListItem>
                          {index < values.additionalApplicants.length - 1 && <Divider />}
                        </React.Fragment>
                      ))}
                    </List>
                  </Grid>

                  {error && (
                    <Grid item xs={12}>
                      <Alert severity="error">{error}</Alert>
                    </Grid>
                  )}

                  <Grid item xs={12}>
                    <Box sx={{ display: 'flex', gap: 2, justifyContent: 'flex-end' }}>
                      <Button
                        variant="outlined"
                        onClick={() => navigate('/client/cases')}
                      >
                        Cancel
                      </Button>
                      <Button
                        type="submit"
                        variant="contained"
                        color="primary"
                      >
                        Create Case
                      </Button>
                    </Box>
                  </Grid>
                </Grid>
              </Form>
            );
          }}
        </Formik>
      </Paper>
    </Box>
  );
};

export default CreateCase;
