import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Formik, Form, Field } from 'formik';
import * as Yup from 'yup';
import {
  Box,
  Typography,
  Paper,
  Grid,
  TextField,
  Button,
  MenuItem,
  FormControl,
  InputLabel,
  Select,
  IconButton,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  Divider,
  Alert,
} from '@mui/material';
import { Delete as DeleteIcon, Add as AddIcon } from '@mui/icons-material';

interface AdditionalApplicant {
  name: string;
  email: string;
}

interface FormValues {
  caseType: string;
  description: string;
  propertyType: string;
  estimatedValue: string;
  loanAmount: string;
  additionalApplicants: AdditionalApplicant[];
}

interface AdditionalApplicantErrors {
  name?: string;
  email?: string;
}

const validationSchema = Yup.object({
  caseType: Yup.string().required('Case type is required'),
  description: Yup.string().required('Description is required'),
  propertyType: Yup.string().required('Property type is required'),
  estimatedValue: Yup.number()
    .required('Estimated value is required')
    .positive('Value must be positive'),
  loanAmount: Yup.number()
    .required('Loan amount is required')
    .positive('Amount must be positive'),
  additionalApplicants: Yup.array().of(
    Yup.object({
      name: Yup.string().required('Name is required'),
      email: Yup.string().email('Invalid email').required('Email is required'),
    })
  ),
});

const initialValues = {
  caseType: '',
  description: '',
  propertyType: '',
  estimatedValue: '',
  loanAmount: '',
  additionalApplicants: [] as AdditionalApplicant[],
};

const CreateCase: React.FC = () => {
  const navigate = useNavigate();
  const [error, setError] = useState<string | null>(null);

  const handleSubmit = async (values: typeof initialValues) => {
    try {
      // In a real application, this would be an API call
      console.log('Form submitted:', values);

      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));

      // Navigate to cases list on success
      navigate('/client/cases');
    } catch (err) {
      setError('Failed to create case. Please try again.');
    }
  };

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        Create New Case
      </Typography>

      <Paper sx={{ p: 3 }}>
        <Formik<FormValues>
          initialValues={initialValues}
          validationSchema={validationSchema}
          onSubmit={handleSubmit}
        >
          {({ values, errors, touched, handleChange, setFieldValue }) => {
            const getApplicantError = (index: number, field: keyof AdditionalApplicant): string | undefined => {
              const error = errors.additionalApplicants?.[index];
              if (typeof error === 'object' && error !== null) {
                return (error as AdditionalApplicantErrors)[field];
              }
              return undefined;
            };

            return (
              <Form>
                <Grid container spacing={3}>
                  <Grid item xs={12}>
                    <FormControl fullWidth error={touched.caseType && !!errors.caseType}>
                      <InputLabel>Case Type</InputLabel>
                      <Select
                        name="caseType"
                        value={values.caseType}
                        onChange={handleChange}
                        label="Case Type"
                      >
                        <MenuItem value="mortgage_application">Mortgage Application</MenuItem>
                        <MenuItem value="remortgage">Remortgage</MenuItem>
                        <MenuItem value="buy_to_let">Buy to Let</MenuItem>
                      </Select>
                      {touched.caseType && errors.caseType && (
                        <Typography color="error" variant="caption">
                          {errors.caseType}
                        </Typography>
                      )}
                    </FormControl>
                  </Grid>

                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      name="description"
                      label="Description"
                      value={values.description}
                      onChange={handleChange}
                      error={touched.description && !!errors.description}
                      helperText={touched.description && errors.description}
                      multiline
                      rows={3}
                    />
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <FormControl fullWidth error={touched.propertyType && !!errors.propertyType}>
                      <InputLabel>Property Type</InputLabel>
                      <Select
                        name="propertyType"
                        value={values.propertyType}
                        onChange={handleChange}
                        label="Property Type"
                      >
                        <MenuItem value="house">House</MenuItem>
                        <MenuItem value="flat">Flat</MenuItem>
                        <MenuItem value="bungalow">Bungalow</MenuItem>
                      </Select>
                      {touched.propertyType && errors.propertyType && (
                        <Typography color="error" variant="caption">
                          {errors.propertyType}
                        </Typography>
                      )}
                    </FormControl>
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <TextField
                      fullWidth
                      name="estimatedValue"
                      label="Estimated Property Value"
                      type="number"
                      value={values.estimatedValue}
                      onChange={handleChange}
                      error={touched.estimatedValue && !!errors.estimatedValue}
                      helperText={touched.estimatedValue && errors.estimatedValue}
                    />
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <TextField
                      fullWidth
                      name="loanAmount"
                      label="Loan Amount"
                      type="number"
                      value={values.loanAmount}
                      onChange={handleChange}
                      error={touched.loanAmount && !!errors.loanAmount}
                      helperText={touched.loanAmount && errors.loanAmount}
                    />
                  </Grid>

                  <Grid item xs={12}>
                    <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                      <Typography variant="h6">Additional Applicants</Typography>
                      <IconButton
                        color="primary"
                        onClick={() => {
                          setFieldValue('additionalApplicants', [
                            ...values.additionalApplicants,
                            { name: '', email: '' },
                          ]);
                        }}
                      >
                        <AddIcon />
                      </IconButton>
                    </Box>

                    <List>
                      {values.additionalApplicants.map((_, index) => (
                        <React.Fragment key={index}>
                          <ListItem>
                            <Grid container spacing={2}>
                              <Grid item xs={12} md={6}>
                                <TextField
                                  fullWidth
                                  name={`additionalApplicants.${index}.name`}
                                  label="Name"
                                  value={values.additionalApplicants[index].name}
                                  onChange={handleChange}
                                  error={
                                    touched.additionalApplicants?.[index]?.name &&
                                    !!getApplicantError(index, 'name')
                                  }
                                  helperText={
                                    touched.additionalApplicants?.[index]?.name &&
                                    getApplicantError(index, 'name')
                                  }
                                />
                              </Grid>
                              <Grid item xs={12} md={6}>
                                <TextField
                                  fullWidth
                                  name={`additionalApplicants.${index}.email`}
                                  label="Email"
                                  type="email"
                                  value={values.additionalApplicants[index].email}
                                  onChange={handleChange}
                                  error={
                                    touched.additionalApplicants?.[index]?.email &&
                                    !!getApplicantError(index, 'email')
                                  }
                                  helperText={
                                    touched.additionalApplicants?.[index]?.email &&
                                    getApplicantError(index, 'email')
                                  }
                                />
                              </Grid>
                            </Grid>
                            <ListItemSecondaryAction>
                              <IconButton
                                edge="end"
                                onClick={() => {
                                  const newApplicants = [...values.additionalApplicants];
                                  newApplicants.splice(index, 1);
                                  setFieldValue('additionalApplicants', newApplicants);
                                }}
                              >
                                <DeleteIcon />
                              </IconButton>
                            </ListItemSecondaryAction>
                          </ListItem>
                          {index < values.additionalApplicants.length - 1 && <Divider />}
                        </React.Fragment>
                      ))}
                    </List>
                  </Grid>

                  {error && (
                    <Grid item xs={12}>
                      <Alert severity="error">{error}</Alert>
                    </Grid>
                  )}

                  <Grid item xs={12}>
                    <Box sx={{ display: 'flex', gap: 2, justifyContent: 'flex-end' }}>
                      <Button
                        variant="outlined"
                        onClick={() => navigate('/client/cases')}
                      >
                        Cancel
                      </Button>
                      <Button
                        type="submit"
                        variant="contained"
                        color="primary"
                      >
                        Create Case
                      </Button>
                    </Box>
                  </Grid>
                </Grid>
              </Form>
            );
          }}
        </Formik>
      </Paper>
    </Box>
  );
};

export default CreateCase;
