import React, { useState } from 'react';
import {
  Box,
  Paper,
  Typography,
  Grid,
  TextField,
  Button,
  Avatar,
  Divider,
  IconButton,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Alert,
  Snackbar,
} from '@mui/material';
import { Edit as EditIcon, Save as SaveIcon, Cancel as CancelIcon } from '@mui/icons-material';
import { Formik, Form, Field } from 'formik';
import * as Yup from 'yup';

interface ProfileData {
  firstName: string;
  lastName: string;
  email: string;
  phone: string;
  dateOfBirth: string;
  address: string;
  postcode: string;
  preferredContactMethod: string;
  preferredAppointmentTime: string;
  marketingPreferences: {
    emailUpdates: boolean;
    smsUpdates: boolean;
    postalUpdates: boolean;
  };
}

const validationSchema = Yup.object({
  firstName: Yup.string().required('First name is required'),
  lastName: Yup.string().required('Last name is required'),
  email: Yup.string().email('Invalid email').required('Email is required'),
  phone: Yup.string()
    .matches(/^[0-9+\s-()]+$/, 'Invalid phone number')
    .required('Phone number is required'),
  dateOfBirth: Yup.date().required('Date of birth is required'),
  address: Yup.string().required('Address is required'),
  postcode: Yup.string()
    .matches(/^[A-Z]{1,2}[0-9][A-Z0-9]? ?[0-9][A-Z]{2}$/i, 'Invalid UK postcode')
    .required('Postcode is required'),
  preferredContactMethod: Yup.string().required('Preferred contact method is required'),
  preferredAppointmentTime: Yup.string().required('Preferred appointment time is required'),
});

const initialValues: ProfileData = {
  firstName: 'John',
  lastName: 'Doe',
  email: 'john.doe@example.com',
  phone: '07700 900123',
  dateOfBirth: '1990-01-01',
  address: '123 Main Street',
  postcode: 'SW1A 1AA',
  preferredContactMethod: 'email',
  preferredAppointmentTime: 'morning',
  marketingPreferences: {
    emailUpdates: true,
    smsUpdates: false,
    postalUpdates: false,
  },
};

const ClientProfile: React.FC = () => {
  const [isEditing, setIsEditing] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);

  const handleSubmit = async (values: ProfileData) => {
    try {
      // In a real application, this would be an API call
      console.log('Profile updated:', values);

      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));

      setIsEditing(false);
      setShowSuccess(true);
    } catch (error) {
      console.error('Failed to update profile:', error);
    }
  };

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        Profile
      </Typography>

      <Paper sx={{ p: 3 }}>
        <Formik
          initialValues={initialValues}
          validationSchema={validationSchema}
          onSubmit={handleSubmit}
        >
          {({ values, errors, touched, handleChange, handleSubmit, resetForm }) => (
            <Form>
              <Grid container spacing={3}>
                {/* Profile Header */}
                <Grid item xs={12}>
                  <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
                    <Avatar
                      sx={{ width: 100, height: 100, mr: 3 }}
                      src="/path-to-profile-image.jpg"
                    />
                    <Box>
                      <Typography variant="h5">
                        {values.firstName} {values.lastName}
                      </Typography>
                      <Typography color="text.secondary">
                        Client since January 2024
                      </Typography>
                    </Box>
                  </Box>
                </Grid>

                {/* Personal Information */}
                <Grid item xs={12}>
                  <Typography variant="h6" gutterBottom>
                    Personal Information
                  </Typography>
                </Grid>

                <Grid item xs={12} md={6}>
                  <TextField
                    fullWidth
                    name="firstName"
                    label="First Name"
                    value={values.firstName}
                    onChange={handleChange}
                    error={touched.firstName && !!errors.firstName}
                    helperText={touched.firstName && errors.firstName}
                    disabled={!isEditing}
                  />
                </Grid>

                <Grid item xs={12} md={6}>
                  <TextField
                    fullWidth
                    name="lastName"
                    label="Last Name"
                    value={values.lastName}
                    onChange={handleChange}
                    error={touched.lastName && !!errors.lastName}
                    helperText={touched.lastName && errors.lastName}
                    disabled={!isEditing}
                  />
                </Grid>

                <Grid item xs={12} md={6}>
                  <TextField
                    fullWidth
                    name="dateOfBirth"
                    label="Date of Birth"
                    type="date"
                    value={values.dateOfBirth}
                    onChange={handleChange}
                    error={touched.dateOfBirth && !!errors.dateOfBirth}
                    helperText={touched.dateOfBirth && errors.dateOfBirth}
                    disabled={!isEditing}
                    InputLabelProps={{ shrink: true }}
                  />
                </Grid>

                <Grid item xs={12}>
                  <Divider sx={{ my: 2 }} />
                </Grid>

                {/* Contact Information */}
                <Grid item xs={12}>
                  <Typography variant="h6" gutterBottom>
                    Contact Information
                  </Typography>
                </Grid>

                <Grid item xs={12} md={6}>
                  <TextField
                    fullWidth
                    name="email"
                    label="Email"
                    type="email"
                    value={values.email}
                    onChange={handleChange}
                    error={touched.email && !!errors.email}
                    helperText={touched.email && errors.email}
                    disabled={!isEditing}
                  />
                </Grid>

                <Grid item xs={12} md={6}>
                  <TextField
                    fullWidth
                    name="phone"
                    label="Phone Number"
                    value={values.phone}
                    onChange={handleChange}
                    error={touched.phone && !!errors.phone}
                    helperText={touched.phone && errors.phone}
                    disabled={!isEditing}
                  />
                </Grid>

                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    name="address"
                    label="Address"
                    value={values.address}
                    onChange={handleChange}
                    error={touched.address && !!errors.address}
                    helperText={touched.address && errors.address}
                    disabled={!isEditing}
                    multiline
                    rows={2}
                  />
                </Grid>

                <Grid item xs={12} md={6}>
                  <TextField
                    fullWidth
                    name="postcode"
                    label="Postcode"
                    value={values.postcode}
                    onChange={handleChange}
                    error={touched.postcode && !!errors.postcode}
                    helperText={touched.postcode && errors.postcode}
                    disabled={!isEditing}
                  />
                </Grid>

                <Grid item xs={12}>
                  <Divider sx={{ my: 2 }} />
                </Grid>

                {/* Preferences */}
                <Grid item xs={12}>
                  <Typography variant="h6" gutterBottom>
                    Preferences
                  </Typography>
                </Grid>

                <Grid item xs={12} md={6}>
                  <FormControl fullWidth>
                    <InputLabel>Preferred Contact Method</InputLabel>
                    <Select
                      name="preferredContactMethod"
                      value={values.preferredContactMethod}
                      onChange={handleChange}
                      label="Preferred Contact Method"
                      disabled={!isEditing}
                    >
                      <MenuItem value="email">Email</MenuItem>
                      <MenuItem value="phone">Phone</MenuItem>
                      <MenuItem value="post">Post</MenuItem>
                    </Select>
                  </FormControl>
                </Grid>

                <Grid item xs={12} md={6}>
                  <FormControl fullWidth>
                    <InputLabel>Preferred Appointment Time</InputLabel>
                    <Select
                      name="preferredAppointmentTime"
                      value={values.preferredAppointmentTime}
                      onChange={handleChange}
                      label="Preferred Appointment Time"
                      disabled={!isEditing}
                    >
                      <MenuItem value="morning">Morning (9am - 12pm)</MenuItem>
                      <MenuItem value="afternoon">Afternoon (12pm - 5pm)</MenuItem>
                      <MenuItem value="evening">Evening (5pm - 8pm)</MenuItem>
                    </Select>
                  </FormControl>
                </Grid>

                {/* Action Buttons */}
                <Grid item xs={12}>
                  <Box sx={{ display: 'flex', gap: 2, justifyContent: 'flex-end' }}>
                    {isEditing ? (
                      <>
                        <Button
                          variant="outlined"
                          startIcon={<CancelIcon />}
                          onClick={() => {
                            resetForm();
                            setIsEditing(false);
                          }}
                        >
                          Cancel
                        </Button>
                        <Button
                          variant="contained"
                          startIcon={<SaveIcon />}
                          onClick={() => handleSubmit()}
                        >
                          Save Changes
                        </Button>
                      </>
                    ) : (
                      <Button
                        variant="contained"
                        startIcon={<EditIcon />}
                        onClick={() => setIsEditing(true)}
                      >
                        Edit Profile
                      </Button>
                    )}
                  </Box>
                </Grid>
              </Grid>
            </Form>
          )}
        </Formik>
      </Paper>

      <Snackbar
        open={showSuccess}
        autoHideDuration={6000}
        onClose={() => setShowSuccess(false)}
      >
        <Alert severity="success" onClose={() => setShowSuccess(false)}>
          Profile updated successfully
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default ClientProfile;
